'',
'',
'    -- Loop through the ORDER collection and insert rows into the Order Line Item table',
'    for i in 1..apex_application.g_f01.count loop',
'      if apex_application.g_f03(i) is not null  then',
'        ',
'        open CHECK_COLLECTION (to_number(apex_application.g_f01(i)),to_number(apex_application.g_f04(i)));',
