,p_process_name=>'Place Order'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_order_id    number;',
'    l_customer_id varchar2(30) := :P20_CUST_ID;',
'    l_balance_id  number;',
'    ',
'    -- Elegxos ean o pelatis exei kartela',
'    CURSOR CHECK_BALANCE_CUST(cust_id IN number) IS',
'       select count(*) from balance where BA_CUSTID = cust_id;',
'       ',
'    l_count_balance number;',
'    ',
'    -- Synolo gia eisagwgi ston BALANCE',
'     CURSOR CET_TOTAL(ord_id IN number) IS',
'       select sum(ORDI_UNIT_PRICE*ORDI_QUANTITY) from order_items where ORDI_ORD_ID = ord_id;',
'       ',
'     v_total number;',
'begin',
'',
'    -- Create New Customer',
'    if :P20_CUSTOMER_OPTIONS = ''NEW'' then',
'        insert into CUSTOMERS (',
'            CUST_NAME,',
'            CUST_SURNAME,',
'            CUST_ADDRESS,',
'            CUST_CITY,',
'            CUST_ZIPCODE,',
'            CUST_EMAIL,',
'            CUST_PHONENUM,',
'            CUST_MOBILENUM',
'        )',
'          values (',
'            :P20_CUST_NAME,',
'            :P20_CUST_SURNAME,',
'            :P20_CUST_ADDRESS,',
'            :P20_CUST_CITY,',
'            :P20_CUST_ZIPCODE,',
'            :P20_CUST_EMAIL,',
'            :P20_CUST_PHONENUM,',
'            :P20_CUST_MOBILENUM)',
'          returning cust_id into l_customer_id;',
'',
'          :P20_CUST_ID := l_customer_id;',
'          ',
'           -- Dimiourgia kartelas ston neo pelati',
'           INSERT INTO BALANCE',
'           (BA_CUSTID, BA_TYPE, BA_DESCR, BA_AMOUNT_UP, BA_AMOUNT_DOWN, BA_DATE)',
'           VALUES',
'           (:P20_CUST_ID, ''F'', ''Δημιουργία Καρτέλας'',0,0,SYSDATE);',
'    end if;',
'    ',
'    open CHECK_BALANCE_CUST(l_customer_id);',
'    fetch CHECK_BALANCE_CUST into l_count_balance;',
'    close CHECK_BALANCE_CUST;',
'',
'    -- Insert a row into the Order Header table',
'    insert into orders(ORD_CUST_ID, ORD_TOTAL, ORD_TIMESTAMP, ORD_USER_NAME, ORD_COMMENTS)',
'       values(l_customer_id, null, systimestamp, upper(:APP_USER), :P22_ORD_COMMENTS) returning ord_id into l_order_id;',
'     ',
'     ',
'    if  l_count_balance = 0 then',
'           -- Dimiourgia kartelas ston neo pelati',
'           INSERT INTO BALANCE',
'           (BA_CUSTID, BA_TYPE, BA_DESCR, BA_AMOUNT_UP, BA_AMOUNT_DOWN, BA_DATE)',
'           VALUES',
'           (l_customer_id, ''F'', ''Δημιουργία Καρτέλας'',0,0,SYSDATE);',
'    end if;',
'    ',
'    insert into balance(BA_CUSTID, BA_TYPE, BA_DATE, BA_DESCR, BA_ORD_ID)',
'       values(l_customer_id, ''P'', SYSDATE, ''Παραγγελία No '' || lpad(to_char(l_order_id),4,''0000''), l_order_id) returning ba_id into l_balance_id;  ',
'    commit;',
'',
'    -- Loop through the ORDER collection and insert rows into the Order Line Item table',
'    for i in',
'    (',
'        select c001 as product_id,',
'               c002 as unit_price,',
'               c003 as quantity,',
'               c004 as warehouse',
'          from apex_collections',
'         where collection_name = ''SDBA_ORDER_ITEMS''',
'           and c003 <> 0',
'         order by 2',
'    )',
'    loop',
'       insert into order_items(ORDI_ORD_ID, ORDI_PRO_ID, ORDI_UNIT_PRICE, ORDI_QUANTITY, ORDI_WARID) ',
'         values (l_order_id, i.product_id, i.unit_price, i.quantity, i.warehouse);',
'       ',
'    end loop;',
'    -------------------------------------------------------------------------------------------------',
'        ',
'',
'    -------------------------------------------------------------------------------------------------',
'    -- Eisagwgi total ston balance',
'    -------------------------------------------------------------------------------------------------',
'    ',
'    open CET_TOTAL(l_order_id);',
'    fetch CET_TOTAL into v_total;',
'    close CET_TOTAL;',
'    ',
'    update BALANCE set BA_AMOUNT_UP = v_total WHERE BA_ORD_ID = l_order_id;',
'    -------------------------------------------------------------------------------------------------',
'    -------------------------------------------------------------------------------------------------    ',
'    commit;',
'',
'    -- Set the item P22_ORDER_ID to the order which was just placed',
'    :P22_ORD_ID := l_order_id;',
'',
'    -- Truncate the collection after the order has been placed',
'    apex_collection.truncate_collection(p_collection_name => ''SDBA_CUSTOMERS'');',
'    apex_collection.truncate_collection(p_collection_name => ''SDBA_ORDER_ITEMS'');',
'end;'))
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_api.id(55084965443989616)
,p_process_success_message=>'Η παραγγελία καταχωρήθηκε'
);
